import React, { useState } from 'react'
import BigButton from '../BigButton'

function LoginInputComponent({handleChange, text, name, type, placeholder, classInput, classLabel, classDiv, classPass, classBtnPass, handleShowPass, textPassBtn, passClean}) {
    
    const [pass, setPass] = useState('')
    
    passClean==='error' && setPass('')
    passClean && console.log('limpiando')
    console.log('PASS STATE => '+ passClean)

    const handleInputChange=(e)=>{
        setPass(e.target.value)
        handleChange(e)
    }

    return (
        <div className={classDiv}>
            <div className={classPass}>
                <label className={classLabel}>{text}</label>
                <BigButton onClickFunction={handleShowPass} text={textPassBtn} cssClass={classBtnPass}/>
            </div>
            <input  type={type} 
                    name={name}
                    placeholder={placeholder}
                    className={classInput} 
                    onChange={handleInputChange}
                    value={pass}
                />
        </div>
    )
}

export default LoginInputComponent
