import React, { useState } from 'react'

function ProfessionalCardInfo({biography, professionalData, schedule}) {
    
    const [proInfo, setproInfo] = useState('bio')
    
    
    const changeState = (e)=>{
        setproInfo(e.target.name)
    }
    
    const bioclass = 'bg-green-200'
    const profileclass = 'bg-yellow-200'
    const scheduleclass = 'bg-red-200'

    return (
        <div class='flex flex-col'>
            <div class=' flex items-center justify-around border-double border-gray-400 border-b-4 pt-2 pb-2'>
                <button name='bio' 
                        class='bg-gray-300 p-1 font-medium rounded-md h-10 w-24'
                        onClick={changeState}
                    >Biography</button>
                <button name='profile' 
                        class='bg-gray-300 p-1 font-medium rounded-md h-10 w-36'
                        onClick={changeState}
                    >Perfil Laboral</button>
                <button name='schedule'
                        class='bg-gray-300 p-1 font-medium rounded-md h-10 w-24'
                        onClick={changeState}
                    >Agenda</button>
            </div>
            <div className='bg-blue-200 items-center justify-center p-10'>
                {proInfo==='bio' && <span class={bioclass}>{biography}</span>}
                {proInfo==='profile' && <span class={profileclass}>{professionalData}</span>}
                {proInfo==='schedule' && schedule?.map((elem, index)=>{
                    /*{date: 'Viernes', available: false}*/
                    return <span key={index} class={scheduleclass}>{elem.available?elem.date:''}</span>
                })}
            </div>
        </div>
    )
}

export default ProfessionalCardInfo
