import React, {useState} from 'react'
import BigButton from '../BigButton'
import LoginInputComponent from './LoginInputComponent'
import { Link } from 'react-router-dom';



function LoginFormComponents({classLogBtn, classSelectBtn, logIn}) {

    const [flag, setflag] = useState(false)
    const [inputName, setinputName] = useState('username')
    const handleFlag=(e)=>{
        setinputName(e.target.name)
        setflag(!flag)
    }

    const [errors, setErrors] = useState({
        userReady:'',
        password:'',
        repassword:''
    })

    const handleErrors=(name)=>{
        if(name==='username' || name==='mail'){
            setErrors({
                ...errors, userReady:'have some error' 
            })
            return;
        }
        if(name==='password'){
            setErrors({
                ...errors, password:'have some error' 
            })
            return
        }
        if(name==='repassword'){
            setErrors({
                ...errors, repassword:'have some error' 
            })
            return
        }
    }

    const handleChange=(e)=>{console.log(e.target.value)}

    const classInput='bg-gray-100 border-2 border-gray-300 px-5 py-2 '

    const classDanger='ring-4 ring-red-500 ring-opacity-50 bg-gray-100 px-5 py-2 '
    
    const classLabel='font-xl '

    const classDiv='flex flex-col items-start justify-around  w-96 h-24 '

    return (
        <div class='flex flex-col'>
            {flag===false &&
                <div class='flex flex-col'>
                    <span>Loguearte con</span>
                    <div class='flex flex-col'>
                        <button onClick={handleFlag}
                                class={classSelectBtn}
                                name='username'
                            >UserName</button>
                        <button onClick={handleFlag}
                                class={classSelectBtn}
                                name='mail'
                            >E-Mail</button>
                    </div>
                </div>
            }
            {flag &&
                <LoginInputComponent    cb={handleChange} 
                text={inputName==='username'?'Nombre de Usuario':'Correo Electrónico'} 
                name={inputName}
                placeholder={inputName==='username'?'Ingrese su Nombre de Usuario':'Ingrese su Correo Electrónico'}
                classInput={classInput}
                classLabel={classLabel}
                classDiv={classDiv}
                />
            }
            {flag &&
                <LoginInputComponent    handleChange={handleChange}
                text={'Ingrese su Contraseña'} 
                name={'password'}
                placeholder={'Ingrese su Contraseña'}
                classInput={classInput}
                classLabel={classLabel}
                classDiv={classDiv}
                />
            }
            {flag &&
                <LoginInputComponent    cb={handleChange} 
                text={'Repita su Contraseña'} 
                name={'repassword'}
                placeholder={'Repita su Contraseña'}
                classInput={classDanger}
                classLabel={classLabel}
                classDiv={classDiv}
                />
            }
            {flag &&
                <BigButton onClickFunction={logIn} text={'Ingresar'} cssClass={classLogBtn}/>
            }
            <div class='flex items-center justify-center mt-10 text-2xl font-semibold  '>
                {flag &&
                    <Link class='hover:no-underline hover:text-red-400 duration-700' to={'/registro'}>Crea tu cuenta!</Link>
                }
            </div>
        </div>
    )
}

export default LoginFormComponents
